import static java.lang.Math.sqrt;public class APRightAngle {    private double height,base;    public APRightAngle(double height, double base) {        this.height=height;        this.base=base;    }    public double getArea() {        return(0.5*height*base);    }    public double getPerimeter() {        return(height+base+(sqrt((height*height)+(base*base))));    }}