public class ArrayOperation {    private int number[],index;    public void setNumbers(int number[],int index) {        this.index=index;        this.number=number;        }    public int evenSum() {        int evenSum=0;        for(int i=0;i<index;i++)            if(number[i]%2==0)                evenSum+=number[i];        return evenSum;        }    public int oddSum() {        int oddSum=0;        for(int i=0;i<index;i++)            if(number[i]%2!=0)                oddSum+=number[i];        return oddSum;        }    public int largest() {        int max=number[0],i=0;        for(;i<index;i++)            if(number[i]>max)                max=number[i];        return max;        }    public int smallest() {        int min=number[0],i=0;        for(;i<index;i++)            if(number[i]<min)                min=number[i];        return min;        }    }