/*  In an office, an employee has two addresses: permanent address and address for correspondence.    An address contains house number, street or colony, area and city.    Model this scenario in a Java program to create an array of employees and print the    name of the employee with both of its addresses searching from the array when the    employee number is given as the input.  */import java.util.ArrayList;import java.util.Scanner;public class EmployeeMain {    public static void main(String[] args) {        boolean flag;        EmployeeData[] edata=null;        String empId, empName, preHouse, preStreet, preArea, preCity, perHouse, perStreet, perArea, perCity;        System.out.println("Address Table...");        Scanner sc = new Scanner(System.in);        Scanner str = new Scanner(System.in);        System.out.println("Enter number of Employees: ");        int empNo = sc.nextInt();        for (int i = 0; i < empNo; i++) {            edata = new EmployeeData[i];            System.out.println("Enter Employee Details: ");            System.out.println("Enter ID & Name of Employee: " + i);            empId = str.next();            empName = str.next();            System.out.println("Enter Present Address for Employee" + i);            preHouse = str.next();            preStreet = str.next();            preArea = str.next();            preCity = str.next();            System.out.println("Enter Permanent Address for Employee: " + i);            perHouse = str.next();            perStreet = str.next();            perArea = str.next();            perCity = str.next();            edata[i] = new EmployeeData(empId, empName, preHouse, preStreet, preArea,                preCity, perHouse, perStreet, perArea, perCity);        }        System.out.println("Enter Employee Number to search details for: ");        String empIds = str.next();        int r=0;        for(int i = 0; i < empNo; i++)        {            if(edata[i].getEmpId() == empIds)            {                flag = true;                r=i;                break;            }            else            {                flag = false;            }            System.out.println("Searching...");        }        if(flag=true)   {            System.out.println("Details for Employee "+empIds+" is: ");            System.out.println(edata[r].getDetails(empIds));        }    }}