import java.util.Scanner;public class IntrestUI {    private Scanner sc;    private SimpleIntrest si;    private CompoundIntrest ci;    private Values v;    public IntrestUI() {        si = new SimpleIntrest();        ci = new CompoundIntrest();        v = new Values();        sc = new Scanner(System.in);    }    public void menu() {        int choice = 0;        v.setValues();        while (choice != 4) {            System.out.println("1. Simple Intrest    2. Compound Intrest    3. Change Values    4. Exit");            System.out.print("Enter your choice : ");            choice = sc.nextInt();            switch (choice) {                case 1:                    double s = si.getSI(v);                    System.out.println("Simple Interest is calculated Monthly");                    System.out.println("Monthly Simple Interest: " + s / v.t);                    double stot = 12 * s;                    System.out.println("Total amount to be paid by stipulated time is: " + (v.p + stot));                    break;                case 2:                    double b = ci.getCI(v);                    double anl = (b - v.p) / (v.t);                    System.out.println("Average annual Compound Interest: " + anl);                    System.out.println("Total amount to be paid by stipulated time is: " + b);                    break;                case 3:                    System.out.println("Enter new Values\n");                    v.setValues();                    break;                case 4:                    System.out.println("Thank you");                    System.exit(0);                default:                    System.out.println("Invalid Choice.. Exiting...");                    break;            }        }    }}