public class Score {    private String paperCode, paperName;    private int paperMark;    public Score(String paperCode, String paperName, int paperMark) {        this.paperCode = paperCode;        this.paperName = paperName;        this.paperMark = paperMark;    }    public String getPaperCode() {        return paperCode;    }    public String getPaperName() {        return paperName;    }    public int getPaperMark() {        return paperMark;    }}