import java.util.Scanner;public class Student {    public static void main(String[] args) {        Score score[];        Scanner sc = new Scanner(System.in);        Scanner st = new Scanner(System.in);        int paperNo;        System.out.println("Enter Student Registration number: ");        String studentID=st.nextLine();        System.out.println("Enter Student Name: ");        String studentName=st.nextLine();        System.out.println("How many Semesters are there ?");        int semNo = sc.nextInt();        for (int i = 0; i < semNo; i++) {            System.out.println("Enter the number of Papers in Semester " + (i + 1));            paperNo = sc.nextInt();            score = new Score[paperNo];            System.out.println("Enter Data of the Student ");            for (int j = 0; j < score.length; j++) {                System.out.println("Paper Code: " + (j + 1));                String paperCode = st.next();                System.out.println("Paper Name: " + (j + 1));                String paperName = st.next();                System.out.println("Paper Mark secured: ");                int paperMark = sc.nextInt();                score[j] = new Score(paperCode, paperName, paperMark);            }            System.out.println("Report for "+studentID+" "+studentName);            for (int j = 0; j < score.length; j++) {                String status;                double total = 0;                if (score[j].getPaperMark() > 35)                    status = "Passed";                else                    status = "Failed";                total += score[j].getPaperMark();                System.out.println("Total Score: " + total);                System.out.println("You have " + status + " in " + score[j].getPaperCode()+" "+ score[j].getPaperName());            }        }    }}