import java.util.Scanner;public class StudentScore {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        System.out.println("Howmany Students are appearing exam ?");        int studentNo = sc.nextInt();        char answer[][] = new char[studentNo][4];        System.out.println("Enter Answers of Students:");        for (int i = 0; i < studentNo; i++) {            System.out.println("Enter Answer of Student " + (i + 1));            for (int j = 0; j < 4; j++) {                System.out.println("Enter Answer of Question " + (j + 1));                answer[i][j] = sc.next().charAt(0);            }        }        System.out.println("Results!!");        McqAnswer result = new McqAnswer(answer, studentNo);        result.getScore();    }}