public class SumAux {    protected int evenSum, oddSum, even, odd,num;    int x[]=new int[num];    public  SumAux(int num) {        this.num = num;    }    public void SumAux(int num) {        for (int i = 0; i < num; i++) {            while (x[i] % 2 == 0) {                even++;                evenSum = evenSum + x[i];            }            while (x[i] % 2 != 0) {                odd++;                oddSum = oddSum + x[i];            }        }        System.out.println("You entered " + odd + " odd numbers and " + even + " even numbers.");        System.out.println("Sum of Even numbers:" + evenSum + "\nSum of Odd Numbers: " + oddSum);    }}