public class TaxCalculate {    private double tax;    public double TaxCalculate(double inc) {        if (inc < 10001) {            tax = 0;            System.out.println("You are in Category A\nYou don't need to file Tax return!!");        } else if (inc > 10000 && inc <= 25000) {            tax = 100;            System.out.println("You are in Category B\nYou need to file Tax return!!");        } else if (inc > 25000 && inc <= 50000) {            tax = 200;            System.out.println("You are in Category C\nYou need to file Tax return!!");        } else if (inc > 50000 && inc <= 75000) {            tax = 300;            System.out.println("You are in Category D\nYou need to file Tax return!!");        } else if (inc > 75000 && inc <= 100000) {            tax = 450;            System.out.println("You are in Category E\nYou need to file Tax return!!");        } else {            tax = 650;            System.out.println("You are in Category F\nYou need to file Tax return!!");        }        return tax;    }}