import java.util.Scanner;public class Word {    public static void main(String[] args) {        int ch=0;        Scanner scr = new Scanner(System.in);        Scanner sc = new Scanner(System.in);        System.out.println("Enter the line of text to be operated: ");        String text = sc.nextLine();        while(ch!=5) {            System.out.println("1. Word Count   2. Search a String  3. Check for Palindrome 4. Modify string    5. Exit");            ch = scr.nextInt();            switch (ch) {                case 1:                    WordLine wl = new WordLine(text);                    System.out.println("Number of Words in Given line: " + wl.wordCount());                    break;                case 2:                    System.out.println("Enter string to search: ");                    String str = sc.nextLine();                    WordSearch ws = new WordSearch(text, str);                    int res = ws.wordSearch();                    if (res == 1)                        System.out.println("Text contains the specified String at position " + ws.getPos());                    else                        System.out.println("Text doesn't not contain the specified String.");                    break;                case 3:                    WordPalindrome wp = new WordPalindrome(text);                    int pal = wp.isPalindrome();                    if (pal == 1)                        System.out.println(text + " is a palindrome");                    else                        System.out.println(text + " is not a palindrome");                    break;                case 4:                    System.out.println("Enter new Text to be updated: ");                    text = sc.nextLine();                    break;                case 5:                    System.out.println("Exiting...");                    break;                default:                    System.out.println("Invalid choice.. Try again...");            }        }    }}