import java.util.Objects;public class WordPalindrome {    private String text;    private String rev="";    public WordPalindrome(String text) {        this.text=text;    }    public int isPalindrome() {        int length = text.length();        for (int i = length - 1; i >= 0; i--)            rev = rev + text.charAt(i);        if (Objects.equals(text,rev))            return 1;        else            return 0;    }}