public class WordSearch {    private String text;    private String str;    private int ret;    private int pos;    public WordSearch(String text, String str) {        this.text = text;        this.str = str;    }    public int wordSearch() {        boolean res = text.contains(str);        if (res==true)            ret= 1;        else            ret= 0;        return ret;    }    public int getPos() {        pos=text.indexOf(str);        return pos;    }}