import java.util.Scanner;public class AdhaarSearchingApp {    public static searchAdhaar.Person findPerson(searchAdhaar.Person[] people, String adhaarNo) {        for (searchAdhaar.Person p : people) {            if (p.getAdhaar().equals(adhaarNo)) {                return p;            }        }        return null;    }    public static void main(String[] args) {        Scanner input = new Scanner(System.in);        searchAdhaar.Person[] people = { new Student("Bikash Das").setAdhaar("340430"),            new Employee("Alice", 43000).setAdhaar("65440"),            new Student("Joy").setAdhaar("87594"),            new Employee("Stefanie Knight", 14932040).setAdhaar("10100")        };        System.out.println(findPerson(people, "87594"));        input.close();    }}