public class BankAccountWithException extends BankAccount {    public BankAccountWithException(String account, double balance) {        super(account, balance);        if (balance < 0)            throw new IllegalArgumentException("Invalid balance given");    }    public void doDebit(double amount) throws InsufficientBlanaceException, IllegalArgumentException {        if (amount < 0)            throw new IllegalArgumentException("Invalid balance `			given");        if ((checkBalance() - amount) < 500)            throw new InsufficientBlanaceException("There is only "+ checkBalance() + " left.");    }}