import java.time.Year;public class Book {    Library_Author[] auth = null;    String title = null;    String isbn = null;    Year pubYear = null;    String publisher = null;    public Book(String bookTitle, String isbn, String publisher, Year pubYear, String[] authNames, String[] country) {        title = bookTitle;        this.isbn = isbn;        this.publisher = publisher;        this.pubYear = pubYear;        auth = new Library_Author[authNames.length];        for (int i = 0; i < auth.length; i++) {            auth[i] = new Library_Author(authNames[i], country[i]);        }    }    public Book(String title, String isbn, String pubName, Year year, Library_Author[] author) {    }    public String[] getAuthNames() {        String[] ret = new String[auth.length];        for (int i = 0; i < ret.length; i++)            ret[i] = auth[i].getName();        return ret;    }    public String getTitle() {        return title;    }    @Override    public String toString(){        return String.format("[ Title: %s, ISBN: %s, PubYear: %s, Author: %s]",title,isbn,pubYear,Library_Author.arrayToString(auth));    }    public String getIsbn() {        return isbn;    }    public String getPublisher() {        return publisher;    }}