import java.util.Scanner;public class BookSelf {    public static void main(String[] args) {        System.out.println("Welcome to Library Management System!!");        Scanner sc=new Scanner(System.in);        Scanner str=new Scanner(System.in);        Author author[];        Book book;        System.out.println("1: Add Book     2: Search Book      3: Quit");        int ch=0;        while(ch!=3) {            ch=sc.nextInt();            switch (ch) {                case 1:                    System.out.println("Enter details of Book to be added..");                    System.out.println("Enter Book Name:");                    String title=str.nextLine();                    System.out.println("Enter Book ISBN No:");                    String isbn=str.nextLine();                    System.out.println("Enter Book Publisher Name:");                    String pubName=str.nextLine();                    System.out.println("Enter Book Publication Year:");                    String year=str.nextLine();                    System.out.println("Enter Publisher Designation:");                    String pubDesig=str.nextLine();                    System.out.println("Enter Publisher Organisation:");                    String pubOrg=str.nextLine();                    System.out.println("Enter Publisher Country:");                    String pubCountry=str.nextLine();                    author= new Author[1];                    author[0]=new Author(pubDesig,pubOrg,pubCountry);                    book=new Book(title,isbn,pubName,year,author);                    break;                case 2:                    System.out.println("Enter details of Book to be fetched..");                    break;                case 3:                    break;                default:                    System.out.println("Invalid choice! Please Enter again.");            }        }    }}