import java.io.BufferedWriter;import java.io.FileWriter;import java.io.IOException;import java.util.Scanner;/** * This program demonstrates how to write characters to a text file * using a BufferedReader for efficiency. * @author www.codejava.net * */public class Insert {    public static void main(String[] args) {        try {            FileWriter writer = new FileWriter("result.txt", true);            BufferedWriter bufferedWriter = new BufferedWriter(writer);            int ch=1;            Scanner sc1 = new Scanner(System.in);            Scanner sc = new Scanner(System.in);            Scanner kb = new Scanner(System.in);            System.out.println("Do you want to add data?? (0/1)");            ch=sc1.nextInt();            while(ch != 1 && ch==0)            {            System.out.println("Enter Name: ");            String name = sc.next();            System.out.println("Enter Regd No: ");            String regdNo = sc.next();            System.out.println("Enter Mark");            double mark = kb.nextDouble();            bufferedWriter.newLine();            bufferedWriter.write(name + " " + regdNo + " " + mark + " " + mark * 9.5 / 10);            bufferedWriter.newLine();            bufferedWriter.write("=============================================");            System.out.println("Do you want to add data?? (y/n)");            ch=sc1.nextInt();        }            bufferedWriter.close();        } catch (IOException e) {            e.printStackTrace();        }    }}