import java.util.ArrayList;import java.util.Arrays;import java.util.Collections;import java.util.Iterator;import java.util.List;public class Library {    List<Book> books = null;    public Library(Book[] mybook) {        books = Arrays.asList(mybook);    }    public Library() {        books = new ArrayList<Book>();    }    public Book[] find(String... author) {        List<Book> booklist = new ArrayList<Book>();        int count = 0;        for (Iterator<Book> iterator = books.iterator(); iterator.hasNext();) {            Book book = (Book) iterator.next();            if (book.getAuthNames().length == author.length) {                for (int i = 0; i < book.getAuthNames().length; i++){                    if(book.getAuthNames()[i].equals(author[i]))                    count++;                else                    break;                }                if (count == author.length)                    booklist.add(book);            }        }        return booklist.toArray(new Book[booklist.size()]);    }}