import java.io.FileNotFoundException;import java.time.Year;import java.util.ArrayList;import java.util.List;import java.util.Scanner;public class LibraryApp {    public static void main(String[] args) throws FileNotFoundException {        List<String> names = new ArrayList<String>();        List<String> countryOfAuthors = new ArrayList<String>();        Library myLibrary = new Library();        String isbn = null, pubYear = null, publisher = null, title = null;        Scanner scan = new Scanner(System.in);        Book foundBooks[] = null;        boolean enterMore = false;        boolean moreAuthor = false;        do {            System.out.print("Enter the title: ");            title = scan.nextLine();            System.out.print("Enter ISBN: ");            isbn = scan.nextLine();            System.out.print("Enter pubYear: ");            pubYear = scan.nextLine();            do {                System.out.print("Enter Author Name");                names.add(scan.nextLine());                System.out.print("Enter the country of Author:");                countryOfAuthors.add(scan.nextLine());                System.out.print("Are There More Authors?(Y/N):");                moreAuthor = (scan.nextLine().charAt(0) == 'n') ? false : true;            } while (moreAuthor);            myLibrary.books.add(new Book(title, isbn, publisher,Year.of(Integer .parseInt(pubYear)),                names.toArray(new String[names.size()]), (countryOfAuthors.toArray(new String[countryOfAuthors.size()]))));            System.out.print("Do you want to Enter More Books (Y/N) :");            enterMore = (scan.nextLine().charAt(0) == 'n') ? false : true;        } while (enterMore);        System.out.print("Enter author name to find available books (\",\" separated): ");        foundBooks = myLibrary.find(scan.nextLine().split(","));        for (Book myBook : foundBooks) {            System.out.println(myBook);        }        scan.close();    }}