public class Library_Author {    String name = null;    String country = null;    public Library_Author(String name, String country) {        this.name = name;        this.country = country;    }    public String getName() {        return name;    }    @Override    public String toString() {        return String.format("[ Name: %s, Country: %s ]", name, country);    }    public static String arrayToString(Library_Author[] myAuthors) {        StringBuffer buff = new StringBuffer();        for (Library_Author auth : myAuthors) {            buff.append(auth + " ");        }        return buff.toString();    }    public String getCountry() {        return country;    }}