package searchAdhaar;public abstract class Person {    String name = null;    String adhaar = null;    public Person(String name) {        this.name = name;    }    public String getName() {        return name;    }    public Person setName(String name) {        this.name = name;        return this;    }    public String getAdhaar() {        if (adhaar == null)            return "the person hasn't yet got adhaar number";        else            return adhaar;    }    public Person setAdhaar(String adhaar) {        this.adhaar = adhaar;        return this;    }}