import java.io.*;import java.util.Scanner;public class Search {    public static void main(String args[]) {        try {            Scanner sc=new Scanner(System.in);            int indexfound=0;            BufferedReader bf = new BufferedReader(new FileReader("result.txt"));            String line;            System.out.println("Enter Name to search: ");            String stringSearch =sc.next();            System.out.println("Searching for " + stringSearch + " in file...");            while ((line = bf.readLine()) != null) {                indexfound = line.indexOf(stringSearch);                if (indexfound > -1) {                    break;                }             }            if (indexfound > -1) {                System.out.println(line);            }            else                System.out.println("Not found..");            bf.close();        } catch (IOException e) {            System.out.println("IO Error Occurred: " + e.toString());        }    }}