package paintedShapes;import paintedShapes.Painter.Paintable;public abstract class Shape {    private Paintable paint = null;    public abstract double getArea();    public abstract double getPerimeter();    public abstract double paintShape();    public abstract String toString();    public Paintable getPaintable() {        return paint;    }    public void setPaintable(Paintable paint) {        this.paint = paint;    }}