import java.util.Scanner;public class Student {    public String name[][] = new String[10][10];    void getDetails(int n)     {        Scanner get = new Scanner(System.in);        int limit=n;        System.out.println("Enter "+limit+" Student Details\n");         for(int i=0;i<limit;i++)        {            System.out.println("Enter Student #"+(i+1)+" Name, Roll Number & Marks:");            for(int j=0;j<3;j++)            {                name[i][j] = get.nextLine();            }        }                 display(limit);    }     void display(int limit)    {        System.out.println("Student Name"+"\t"+"Roll"+"\t\t"+"Marks");        for(int i=0;i<limit;i++)        {            for(int j=0;j<3;j++)            {                System.out.print(name[i][j]+"\t\t");            }        System.out.println();        }    }          } 