import java.io.*;import java.util.Scanner;public class Student_Result_App {    public static void main(String[] args) throws IOException {        Scanner in  =  new Scanner(System.in);        System.out.print("Enter Number of Students:");        int n = in.nextInt();        Student std = new Student();        std.getDetails(n);            PrintStream output = new PrintStream(new File("StudentDetails.txt"));            output.println("Student Name"+"\t"+"Roll"+"\t\t"+"Marks");            output.println("======================================");            for(int i=0;i<n;i++)            {                for(int j=0;j<3;j++)                {                    output.print(std.name[i][j]+"\t\t");                }            output.println();            output.println("======================================");            }            output.close();    }    }