import java.applet.Applet;import java.awt.*;import java.awt.event.*;public class WritingApplet extends Applet implements KeyListener, MouseListener {    String msg="";    int x=0,y=0;    public void paint(Graphics g) {        g.drawString(msg, x, y);    }    public void init() {        addKeyListener(this);        addMouseListener(this);    }    public void keyTyped(KeyEvent e) {        msg=msg+e.getKeyChar();        repaint();    }    public void keyPressed(KeyEvent e) {    }    public void keyReleased(KeyEvent e) {    }    public void mouseClicked(MouseEvent e) {        x=getX();        y=getY();        msg="*";        repaint();    }    public void mousePressed(MouseEvent e) {    }    public void mouseReleased(MouseEvent e) {    }    public void mouseEntered(MouseEvent e) {    }    public void mouseExited(MouseEvent e) {    }}